    /**行xml中获取消息列表*/
    public static List<String> getByerMessageFromXML(String html){
        if(StringUtils.isEmpty(html)){return null;}
        if(html.startsWith("&lt;")){
            html=StringEscapeUtils.unescapeHtml(html);
        }
        html=StringUtils.replaceOnce(html,"<![CDATA[","");
        //html=StringUtils.replaceOnce(html,"]]>","");
        html = StringUtils.removeStart(html, "<![CDATA[");
        html = StringUtils.removeEnd(html, "]]>");
        List<String> strings=new ArrayList<String>();
       // if(1==1){strings.add(html);return strings;}


        Document document = Jsoup.parse(html);


        Element endD=document.getElementById("end_delimiter_header");
        if (endD!=null){
            //String eid = endD.nextElementSibling().id();
            String siblTagName = endD.nextElementSibling().tagName();
            if("table".equalsIgnoreCase(siblTagName)){
                Element tmptable= null;
                try {
                    Document tmpdocument = Jsoup.parse(html);
                    tmptable = tmpdocument.getElementById("end_delimiter_header").nextElementSibling();
                    Elements xt= tmptable.getElementsByAttributeValue("id", "TextCTA");
                    xt.remove();
                    Element xr= tmptable.getElementById("RawHtmlText");
                    Elements xrs= xr.getElementsByAttributeValue("id", "RawHtmlText");
                    for(int i=0;i<xrs.size();i++){
                        if (i!=0){
                            xrs.get(i).html("");
                        }
                    }
                    strings.add(tmptable.outerHtml());
                } catch (Exception e) {
                    strings.add(endD.nextElementSibling().outerHtml());
                    logger.error(e);
                }


                //Element rbtn = document.getElementById("ReplyBtn");
                /*String ptagname = rbtn.parent().tagName();
                if("td".equalsIgnoreCase(ptagname)){
                    rbtn.parent().remove();
                }else {
                    rbtn.remove();
                }*/
                //strings.add(tmptable.outerHtml());
                //strings.add(endD.nextElementSibling().outerHtml());


            }
        }


        Element rbtn = document.getElementById("ReplyBtn");
       // String ptagname = rbtn.parent().tagName();
//        rbtn.remove();

        Elements x= document.getElementsByAttributeValue("id", "TextCTA");
        for (Element element : x){
            Elements xxxx=element.siblingElements();

            Elements font=element.getElementsByTag("table");
            if(font!=null&&font.size()>0){
                StringBuffer messageBox=new StringBuffer();

                Element ep=element.previousElementSibling();
                String epid=ep.attr("id");
                if(StringUtils.isEmpty(epid)){
                    messageBox.append(ep.outerHtml());
                }else if ("RawHtmlText".equalsIgnoreCase(epid)){
                    messageBox.append(ep.html());
                }
                messageBox.append(font.get(0).outerHtml());

                //**解析附件*/
                //HtmlUtil htmlUtil=new HtmlUtil();
                Element att = filterEleByID("ImagePreview",element.siblingElements());
                if (att!=null){
                    messageBox.append(att.outerHtml());
                }
                /**商品详情*/
                //Element item = htmlUtil.findEStillEnd(element,"ItemDetails"); //filterEleByID("ItemDetails",element.siblingElements());
                //if(item!=null){
                    //messageBox.append(item.outerHtml());
                //}

                strings.add(messageBox.toString());
            }

            //System.out.println(element.previousElementSibling().outerHtml());
            //System.out.println(element.outerHtml());
        }
        return strings;
    }



    /**根据指定id筛选元素*/
    private static Element filterEleByID(String id,Elements es){
        if (es==null){return null;}
        for (Element e :es){
            String epid=e.attr("id");
            if (id.equalsIgnoreCase(epid)){
                return e;
            }
        }
        return null;
    }

    /**根据id名一直往后找元素，知道结束*/
    private Element findEStillEnd(Element e,String id){
        int i=0;
        while (true){
            i++;
            if(i>1000){return null;}
            Element ee= e.nextElementSibling();
            if(ee==null){return null;}
            String epid=ee.attr("id");
            if (id.equalsIgnoreCase(epid)){
                return ee;
            }
            e=ee;
        }
       // return null;
    }

