mapper.select:
select AddMemberMessageAAQToPartner.*,item.sku sku,count(*) countNum from trading_order_addmembermessageaaqtopartner AddMemberMessageAAQToPartner
left join trading_order_get_item item on AddMemberMessageAAQToPartner.itemid=item.itemID where 1=1

    and AddMemberMessageAAQToPartner.id = #{id}



        /* and MessageGetmymessage.sender=order1.buyer1 and MessageGetmymessage.recipientUserID=order1.seller1 and MessageGetmymessage.itemID=order1.itemid1*/
        /*and MessageGetmymessage.itemID  in (select trading_order_get_orders.itemID from trading_order_get_orders)*/
        and AddMemberMessageAAQToPartner.itemid in (select Partner.itemid from trading_order_addmembermessageaaqtopartner Partner
        left join trading_order_get_item item1 on Partner.itemid=item1.itemID
        left join trading_order_get_orders order3 on Partner.itemid=order3.itemID where Partner.recipientID=order3.buyerUserID
        and Partner.sender=order3.sellerUserID and  Partner.itemid=order3.itemID)


        and (AddMemberMessageAAQToPartner.itemid not in (select Partner.itemid from trading_order_addmembermessageaaqtopartner Partner
        left join trading_order_get_item item1 on Partner.itemid=item1.itemID
        left join trading_order_get_orders order3 on Partner.itemid=order3.itemID where Partner.recipientID=order3.buyerUserID
        and Partner.sender=order3.sellerUserID and  Partner.itemid=order3.itemID) or AddMemberMessageAAQToPartner.itemid is null)



    and AddMemberMessageAAQToPartner.create_time >= #{starttime}


    and AddMemberMessageAAQToPartner.create_time < #{endtime}


    and AddMemberMessageAAQToPartner.messagetype = #{messageType}


    and AddMemberMessageAAQToPartner.replied=#{replied}



        and AddMemberMessageAAQToPartner.sender like "%"#{content}"%"


        and AddMemberMessageAAQToPartner.subject like "%"#{content}"%"


        and item.sku like "%"#{content}"%"



    and AddMemberMessageAAQToPartner.sender in

        #{ebay.ebayName}


group by AddMemberMessageAAQToPartner.messagetype,AddMemberMessageAAQToPartner.transactionId,AddMemberMessageAAQToPartner.sender
