select MessageGetmymessage.* from trading_message_getmymessage MessageGetmymessage
/*left join usercontroller_ebay_account ebay on MessageGetmymessage.ebay_account_id=ebay.ebay_name*/ where 1=1

    and MessageGetmymessage.id = #{id}

select MessageGetmymessage.*,item.sku sku,count(*) countNum from trading_message_getmymessage MessageGetmymessage left join trading_order_get_item item on MessageGetmymessage.itemID=item.itemID
where 1=1
/* and MessageGetmymessage.sender=order1.buyer1 and MessageGetmymessage.recipientUserID=order1.seller1 and MessageGetmymessage.itemID=order1.itemid1*/ /*and MessageGetmymessage.itemID in (select trading_order_get_orders.itemID from trading_order_get_orders)*/ and MessageGetmymessage.itemID in (select message.itemID from trading_message_getmymessage message left join trading_order_get_item item1 on message.itemID=item1.itemID left join trading_order_get_orders order3 on message.itemID=order3.itemID where message.sender=order3.buyerUserID and message.recipientUserID=order3.sellerUserID and message.itemID=order3.itemID)
and (MessageGetmymessage.itemID not in (select message.itemID from trading_message_getmymessage message left join trading_order_get_item item1 on message.itemID=item1.itemID left join trading_order_get_orders order3 on message.itemID=order3.itemID where message.sender=order3.buyerUserID and message.recipientUserID=order3.sellerUserID and message.itemID=order3.itemID) or MessageGetmymessage.itemID is null)
and MessageGetmymessage.id = #{id}
and MessageGetmymessage.receiveDate >= #{starttime}
and MessageGetmymessage.receiveDate < #{endtime}
and MessageGetmymessage.read = #{read}
and MessageGetmymessage.sender = "eBay"
and MessageGetmymessage.sender !="eBay"
and MessageGetmymessage.sender like "%"#{content}"%"
and MessageGetmymessage.subject like "%"#{content}"%"
and item.sku like "%"#{content}"%"
and MessageGetmymessage.ebay_account_id in
#{ebay.id}
and MessageGetmymessage.read="true"
and MessageGetmymessage.read="false"
group by MessageGetmymessage.messageID
order by MessageGetmymessage.receiveDate

 select MessageGetmymessage.* from trading_message_getmymessage MessageGetmymessage
/* left join usercontroller_ebay_account ebay on MessageGetmymessage.ebay_account_id=ebay.id*/ where 1=1

     and MessageGetmymessage.messageID = #{messageID}

 order by MessageGetmymessage.receiveDate
