mapper.select:
select m.id,m.message_to,m.message_from,m.message_type,m.create_time,m.readed,m.message_title,m.message, fu.user_name fromName,tu.user_name toName from public_sitemessage m LEFT JOIN usercontroller_user fu on fu.user_id=m.message_from LEFT JOIN usercontroller_user tu on tu.user_id=m.message_to where 1=1
and m.message_to = #{messageTo}
and (m.message_type = CONCAT(#{messageType},'_SUCCESS'),m.message_type = CONCAT(#{messageType},'_Fail'))
and m.message_from = #{messageFrom}

select m.message_type mtype,count(1) mnum from public_sitemessage m
where (m.readed is null or m.readed='0') and m.message_to=#{mToId}
GROUP BY m.message_type

mapper.update:
update public_sitemessage ps set ps.readed=1 where ps.message_to=#{mToId} and ps.readed=0
and ps.id in
#{item,jdbcType=DECIMAL}
